<script>
	$entertl = gsap.timeline({
		paused: true,
	})

	let $pop

	if (device != 'mobile') {
		$pop = $("#popsvg g")
	}else{
		$pop = $("#popsvg-mobile g")
	}

	$entertl.from($pop, {
		delay: 1,
		duration: 0.8,
		scale: 0.5,
		opacity: 0,
		transformOrigin: 'center',
	  ease: "back.out",
		stagger: {
		  each: .056,
		  from: "random",
		  ease: 'power1.out',
		},
	}).to($pop, {
		delay: 0.5,
		duration: 0.8,
		opacity: 0,
		stagger: {
		  each: .087,
		  from: "random",
		},
	}).from(".enter-stroke", {
		duration: 1,
		drawSVG: "0% 0%",
	}).from(".enter-fade", {
		duration: 1,
		opacity: 0,
	}).from("#scrolldown", {
		duration: 1,
		opacity: 0,
		y: -40,
	})


	$entertl.play()







	let _w = $("#horizontalWrap").outerWidth(true)
	$("#horizontalSpace").height(_w)




	let $scrolltl = gsap.timeline({
		paused: true
	}).to("#horizontalWrap", {
		duration: 5,
		x: -_w + $(window).width(),
		ease: 'none',
	}).from("#drawme", {
		duration: 4,
		drawSVG: "0% 0%",
		ease: "expoScale(0.3,0.8,none)",
	}, 0).to("#drawsvg", {
		duration: 1,
		opacity: (device != 'mobile') ? 0 : 1,
	}, 3.5).from("#horin-bg", {
		duration: 1,
		opacity: 0,
		ease: "none"
	}, 4)


	ScrollTrigger.create({
		toggleActions: "play pause resume reverse", //重覆觸發
		trigger: "#horizontalSpace",
		start: "top 0%",
		end: "bottom 100%",
		// markers: true,
		scrub: true,
		animation: $scrolltl,
		onUpdate: (self) => {}
	});







	function projectHandler(el) {
		let dragging = false;

		const $carousel = $(el).flickity({
			"prevNextButtons": false,
			"pageDots": false,
			"wrapAround": true,
			"autoPlay": false,
			"cellAlign": "center",
			"imagesLoaded": true,
			"pauseAutoPlayOnHover": false,
			"arrowShape": ""
		}).on( 'dragStart.flickity', function( event, pointer ) {
			dragging = true
		}).on( 'dragEnd.flickity', function( event, pointer ) {
			gsap.delayedCall(0.2, function () {
				dragging = false
			});
		}).on( 'pointerUp.flickity', function( event, pointer ) {
			if (dragging) {
				return false
			}

			fancyOpen()
		})


		$("#prev").on("click", function () {
			$carousel.flickity('previous');
			$carousel.flickity('stopPlayer');
		})

		$("#next").on("click", function () {
			$carousel.flickity('next');
			$carousel.flickity('stopPlayer');
		})


		var cellElements = $carousel.flickity('getCellElements');


		$carousel.on( 'change.flickity', function( event, index ) {
			$("#now").text( padLeft(index + 1, 2) );
		});


		$("#total").text( padLeft(cellElements.length, 2) );
	}






	function fancyOpen(el) {
		$(".m-fancyWrap").addClass("is-show").scrollTop(0)
		$("body").addClass("is-lock")
	}

	function fancyClose(el) {
		$(".m-fancyWrap").removeClass("is-show")
		gsap.delayedCall(0.5, function () {
			$("body").removeClass("is-lock")
		});
	}





	function fancySliderHandler(el) {
		const $carousel = $(el).flickity({
			"prevNextButtons": false,
			"pageDots": false,
			"wrapAround": true,
			"autoPlay": false,
			"cellAlign": "center",
			"imagesLoaded": true,
			"pauseAutoPlayOnHover": false,
			"arrowShape": ""
		})


		$("#fancy-prev").on("click", function () {
			$carousel.flickity('previous');
			$carousel.flickity('stopPlayer');
		})

		$("#fancy-next").on("click", function () {
			$carousel.flickity('next');
			$carousel.flickity('stopPlayer');
		})


		var cellElements = $carousel.flickity('getCellElements');


		$carousel.on( 'change.flickity', function( event, index ) {
			$("#fancy-now").text( padLeft(index + 1, 2) );
		});


		$("#fancy-total").text( padLeft(cellElements.length, 2) );
	}









	// desktop doll
	$("#dollWrap").children().on("click", function () {
		let i = $(this).index()

		$("#doll-articleWrap").children().eq(i).addClass("current").siblings().removeClass("current")
	})


	$(".doll-close").on("click", function () {
		$("#doll-articleWrap").children().removeClass("current")
	})





	// mobile doll
	$("#mobile-dollWrap").children().on("click", function () {
		let i = $(this).index()

		$("#mobile-doll-articleWrap").children().eq(i).addClass("current").siblings().removeClass("current")
	})


	$(".mobile-doll-close").on("click", function () {
		$("#mobile-doll-articleWrap").children().removeClass("current")
	})
</script>